import React, { useContext, useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import { useNavigate } from "react-router-dom";
import { ThemeContext } from "../App";

export default function Login() {

  const themeCont = useContext(ThemeContext);
  const colorFont = (themeCont == 'dark') ?'modal show  text-light':'modal show text-dark';
  const navigate = useNavigate();
  const onClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    navigate("/MyApp");
    event.preventDefault();
  };
  const [show, setShow] = useState(false);
  const handleClose = () => navigate("/MyApp");

  return (
    //<div className={`form-check form-switch text-${colorLabel}`}></div>
    <div
      className={colorFont}
      data-bs-theme={themeCont}
      style={{ display: "block", position: "initial"  }}
    >
      <Modal.Dialog>
        <Modal.Header>
            <h3>Sign In</h3>
        </Modal.Header>

        <Modal.Body>
          <div className="Auth-form-container ">
            <form className="Auth-form">
              <div className="Auth-form-content">
                <div className="form-group mt-3">
                  <label>Email address</label>
                  <input
                    type="email"
                    className="form-control mt-1"
                    placeholder="Enter email"
                  />
                </div>
                <div className="form-group mt-3">
                  <label>Password</label>
                  <input
                    type="password"
                    className="form-control mt-1"
                    placeholder="Enter password"
                  />
                </div>
              </div>
            </form>
          </div>
        </Modal.Body>

        <Modal.Footer>
            <button type="submit" className="btn btn-primary" onClick={onClick}>
              Submit
            </button>
            <button type="submit" className="btn btn-primary" onClick={handleClose}>
              exit
            </button>
          <p className="forgot-password text-right mt-2">
            Forgot <a href="/Registration">password?</a>
          </p>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
  )
}
