import { Children, createContext, useContext, useState } from "react";
import { isElementAccessExpression } from "typescript";

type shema = "light" | "dark";

type iContext = {
  color: shema;
  updateTodo: () => void;
};

const defaultState: iContext = {
  color: "dark",
  updateTodo: () => {},
};

const ThemeContext = createContext<iContext>(defaultState);

type Props = {
  title?: string;
  children: string | JSX.Element | JSX.Element[];
};

export default function MyApp() {
  const themeCont = useContext(ThemeContext);
  const [theme, setTheme] = useState<shema>('dark');

  themeCont.updateTodo = () => {
    if (themeCont.color === "light") {
        themeCont.color = "dark";
        setTheme("dark");
    } else {
        themeCont.color = "light";
        setTheme("light");
    }
  };

  return (
    <ThemeContext.Provider value={themeCont}>
      <Form />
      <label>
        <input
          type="checkbox"
          checked={themeCont.color == 'dark'}
          onChange={(e) => {
            themeCont.updateTodo();
          }}
        />
        Use dark mode
      </label>
    </ThemeContext.Provider>
  );
}

function CheckBox({children}:Props){
    const themeCont = useContext(ThemeContext);

    return(
        <input
          type="checkbox"
          checked={themeCont.color == 'dark'}
          onChange={(e) => {
            themeCont.updateTodo();
          }}
        />
    );
}


function Form() {
  const t = useContext(ThemeContext);
  return (
    <Panel title={t.color}>
      <input type="text" id="inputField" />
      <Button>ChangeTheme</Button>
      <CheckBox>CheckBox</CheckBox> 
    </Panel>
  );
}

function Panel({ title, children }: Props) {
  const themeContext = useContext(ThemeContext);
  const className = "panel-" + {title};
  return (
    <section className={className}>
      <h1>{title}</h1>
      {children}
    </section>
  );
}

function Button({ children }: Props) {
  const themeContext = useContext<iContext>(ThemeContext);
  const className = "button-" + themeContext.color;
  const onClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    themeContext.updateTodo();
    event.preventDefault();
  };
  return (
    <button className={className} onClick={onClick}>
      {children}
    </button>
  );
}
