export interface iShema {
  name: "dark1" | "dark2" |"light" ;
}
type tMenu = {
  name: string;
  linkComp: string;
};

const listMenu: tMenu[] = [
  { name: "/MyApp", linkComp: "Home" },
  { name: "/Page2", linkComp: "Features" },
  { name: "/Login", linkComp: "Login" },
];

const Menu = () => {
  return listMenu.map((item) => {
    return <Nav.Link href={item.name}>{item.linkComp}</Nav.Link>;
  });
};

function ColorSchemesExample(props: iShema) {
  const themeCont = useContext(ThemeContext);
  const [theme, setTheme] = useState<tTheme>("dark");

  themeCont.updateTodo = (theme?: tTheme) => {
    if (theme == null) {
      if (themeCont.color === "light") {
        themeCont.color = "dark";
        setTheme("dark");
      } else {
        themeCont.color = "light";
        setTheme("light");
      }
    }else{
        themeCont.color = theme;
        alert(theme);
    }
  };

  const project = () => {
    switch (props.name) {
      case "dark1":
        return (
          <Navbar bg="dark" data-bs-theme="dark">
            <Container>
              <Navbar.Brand href="#home">Navbar</Navbar.Brand>
              <Nav className="me-auto">{Menu()}</Nav>
            </Container>


          <div className={`form-check form-switch text-light`}>
            <input className="form-check-input" type="checkbox" id="flexSwitchCheckDefault" />
            <label className="form-check-label" htmlFor="flexSwitchCheckDefault">dark</label>
          </div>

            <Button
              variant="dark"
              style={{ display: "flex", justifyContent: "right" }}
            >
              Login
            </Button>

          </Navbar>
        );
      case "dark2":
        return (
          <Navbar bg="primary" data-bs-theme="dark">
            <Container>
              <Navbar.Brand href="#home">Navbar</Navbar.Brand>
              <Nav className="me-auto">
                {Menu()}
              </Nav>
            </Container>
            <Button
              variant="info"
              style={{ display: "flex", justifyContent: "right" }}
            >
              Login
            </Button>
          </Navbar>
        );
      case "light":
        return (
          <Navbar bg="light" data-bs-theme="light">
            <Container>
              <Navbar.Brand href="#home">Navbar</Navbar.Brand>
              <Nav className="me-auto">
              {Menu()}
              </Nav>
            </Container>

            <div className={`form-check form-switch text-dark`}>
            <input className="form-check-input" type="checkbox" id="flexSwitchCheckDefault" />
            <label className="form-check-label" htmlFor="flexSwitchCheckDefault">dark</label>
          </div>

            <Button
              variant="light"
              style={{ display: "flex", justifyContent: "right" }}
            >
               <img src={login_black} alt="React Logo" />
              Login
            </Button>
          </Navbar>
        );
      default:
        return <h1>No project match</h1>;
    }
  };
  return (
    <>
      <div>{project()} </div>
    </>
  );
}



export default ColorSchemesExample;
