import React, { useState } from "react";
import { Button, ButtonGroup, Col, Form, Row } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { useAuthContext } from "../Hooks/useAuthContext";

function Login() {

    const { auth, setAuth } = useAuthContext();

    const navigate = useNavigate();

    const onClickCancel = (event: React.MouseEvent<HTMLButtonElement>) => {
    navigate("/MyApp");
    event.preventDefault();
  };
  const onClickReg = (event: React.MouseEvent<HTMLButtonElement>) => {
    navigate("/Registration");
    event.preventDefault();
  };  

  const onClickOK = (event: React.MouseEvent<HTMLButtonElement>)=>{
    var login = document.getElementById("login") as HTMLInputElement | null;
    var password = document.getElementById("password") as HTMLInputElement | null;
    alert(login);
    alert(password);
    event.preventDefault();
  }
  return (
    <div className="form">
      <div className="form-body">
        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
          <Form.Label>Login</Form.Label>
          <Form.Control size="lg" type="email" placeholder="name@example.com" id="login"/>
        </Form.Group>

        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
          <Form.Label  >Password</Form.Label>
          <Form.Control size="lg" type="password" id="password" />
        </Form.Group>
        <div>{auth.login}</div>
        </div>
        {/* <div className="btn-group d-flex" role="group"> */}
        <Row>
        <Col className="btn-group d-flex"><Button variant="primary" size="lg" onClick={onClickOK}>
            Ok
        </Button></Col>
        <Col className="btn-group d-flex"><Button variant="primary" size="lg" onClick={onClickCancel}>
            Cancel
        </Button></Col>
        <Col className="btn-group d-flex"><Button variant="primary" size="lg" onClick={onClickReg}>
            Register
        </Button></Col>
      </Row>
    </div>
  );
}

export default Login;
