import React, { createContext } from "react";
import logo from "./logo.svg";
import "./App.css";
import MyApp from "./Components/Comp";
import User from "./Components/User";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import NotFound from "./Components/NotFound";
import NavbarBS from "./Components/NavbarBS";
import MenuUser from "./Components/MenuUser";
import Login from "./Components/Login";
import { useTheme } from "./Hooks/useTheme";
import notFound from "./Components/NotFound";
import { title } from "process";
import { App1, AppReg, AppAbout, AppMain } from "./HOC/test";
import { App2, Registration } from "./HOC/withTheme";
import Fullscreen from 'react-fullscreen-crossbrowser';


export const ThemeContext = createContext<string>('');

function App() {


  const { theme, setTheme } = useTheme();
  const updateTheme = () => {
    if (theme == 'light')
      setTheme('dark');
     else
     setTheme('light');
  };

  const themeCont = theme;
  const colorFont =
    themeCont == "dark" ? "light" : "dark";

  const bgColor =
    themeCont == "dark" ? "bg-dark" : "bg-lig"

    const textColor =
    themeCont == "dark" ? "text-black" : "text-white"

  function withTheme(NotFound: () => React.JSX.Element): React.ReactNode {
    throw new Error("Function not implemented.");
  }

  return (
    <div className={theme}>
    <div 
    className="p-3 mb-2 {bgColor} {textColor}">
    <ThemeContext.Provider value={theme}>
    <BrowserRouter>
      <MenuUser theme={theme} updateTheme={updateTheme} />
      <Routes>
        <Route path="/MyApp" element={<AppMain name = {theme}/>} />
        <Route path="/Login" element={<Login />} />
        <Route path="/About" element={<AppAbout name = {theme} />} />
        <Route path="/Registration" element={<AppReg name={theme} />} />
        <Route path="*" element={<App2  name={theme}/>} />

        {/* <Route path="*" element={<a = ()=>withTheme(NotFound)  name={theme}/>} /> */}
      </Routes>
    </BrowserRouter>

    </ThemeContext.Provider>
    </div>
    </div>
  );
}

export default App;
