import Card from 'react-bootstrap/Card';
import { useContext, useState } from 'react';
import React from 'react';
import NotFound from '../Components/NotFound';
import { ThemeContext } from '../App';

interface iTheme{
    name: string
}

function withTheme(ChildComp: React.ComponentType<any | string>) {
  return class Component<T> extends React.Component<T & iTheme> {
    render() {
      const { name, ...childProps } = this.props;
      //const themeCont1 = useContext(ThemeContext);
      //const [themeCont,setTheme] = useState('dark');
      const themeCont = name;
      const colorFont =
        themeCont == "dark" ? "modal show  text-light" : "modal show text-dark";

      return (
        <div className="mh-100 mh-100">
          <Card bg={themeCont} data-bs-theme={themeCont} text="danger">
            <Card.Body>
              <ChildComp childProps />
            </Card.Body>
          </Card>
        </div>
      );
    }
  };
};

export const App2 = withTheme(NotFound);
