import React from "react";
import MyApp, { Props } from "../Components/Comp";
import { MDBCard, MDBCardBody, MDBContainer } from "mdb-react-ui-kit";
import { title } from "process";

// interface ContainerProps {
//   areYouCool: boolean;
//   children?: JSX.Element;
// }

interface AwaysCoolStateProps extends Props{
  text?: string;
}

function youreAlwaysCoolHOC(ChildComp: React.ComponentType<any | string>) {
  return class Component extends React.Component<AwaysCoolStateProps> {
     state = {
      areYouCool: false,
    };

    handleClick = () => this.setState({ areYouCool: true });

    render() {
      const { text, ...compProps } = this.props;  
      return (
        <>
          {/* <button onClick={this.handleClick}>Find yout if you're cool</button>
          */}
          <MDBContainer>
            <MDBCard>
              <MDBCardBody>
                {text} 
                <ChildComp areYouCool={this.state.areYouCool} compProps />
                </MDBCardBody>
            </MDBCard>
          </MDBContainer>
        </>
      );
    }
  };
}

// const Container: React.FC<ContainerProps> = (props) => (
//   <h1>{props.areYouCool ? "Heck yes I'm cool! :)" : ":("}</h1>
// );

export const App1 = youreAlwaysCoolHOC(MyApp);
