import { createContext, useContext, useState } from 'react';
import { isElementAccessExpression } from 'typescript';

type shema = 'light' | 'dark';

type iContext = 
{
    color :shema
    updateTodo: () => void;
}

const defaultState: iContext = {
    color:'dark' ,
    updateTodo: () => {},
  };

const ThemeContext = createContext<iContext>(defaultState);

type Props = {
    title?:string
    children: string | JSX.Element | JSX.Element[] 
  }


  export default function MyApp() {

    const themeCont = useContext(ThemeContext);
    const [theme, setTheme] = useState<shema>(themeCont.color);


    themeCont.updateTodo=()=>{
                          //alert(themeCont.color);
                          if (themeCont.color === 'light')
                          {
                            themeCont.color = 'dark';
                            setTheme('dark');
                          } 
                          else{
                            themeCont.color = 'light';
                            setTheme('light');
                          };
                        };

    return (
        <ThemeContext.Provider value={themeCont}>
            <Form />
            <label>
                <input
                    type="checkbox"
                    checked={theme == 'dark'}
                    onChange={(e) => {
                        themeCont.updateTodo();
                    }}
                />
                Use dark mode
            </label>
        </ThemeContext.Provider>
    );
}

function Form() {
    const t = useContext(ThemeContext)
    return (
        <Panel title="Welcome">
            <input type="text" id="inputField" />
            <Button>Sign up</Button>
            <Button>Log in</Button>
        </Panel>
    );
}

function Panel({ title, children }:Props) {
    const theme = useContext(ThemeContext);
    const className = 'panel-' + theme.color;
    return (
        <section className={className}>
            <h1>{title}</h1>
            {children}
        </section>
    );
}

function Button({ children }:Props) {
    //const theme = 'fff';
    const theme = useContext<iContext>(ThemeContext);
    const className = 'button-' + theme.color;
    const onClick  = (event: React.MouseEvent<HTMLButtonElement>) => 
    {
      var inputField = document.getElementById("inputField") as HTMLInputElement | null;;
      if (inputField != null) {
        //alert(inputField.value); // ðŸ‘‰ "Initial Value";
        //setTheme('dark');
        alert(theme.color);
        }
      event.preventDefault();
    }
    return (
        <button className={className} onClick={onClick}>{children}</button>
    );
}
