interface iTheme{
    name: string
}


interface AwaysCoolStateProps {
  text?: string;
}

function youreAlwaysCoolHOC(ChildComp: React.ComponentType<any | string>) {
  return class Component extends React.Component<AwaysCoolStateProps & Props> {
     state = {
      areYouCool: false,
    };

    handleClick = () => this.setState({ areYouCool: true });

    render() {
      const { text, ...compProps } = this.props;  
      return (
        <>
          {/* <button onClick={this.handleClick}>Find yout if you're cool</button>
          */}
          <MDBContainer>
            <MDBCard>
              <MDBCardBody>
                {text} 
                <ChildComp areYouCool={this.state.areYouCool} compProps />
                </MDBCardBody>
            </MDBCard>
          </MDBContainer>
        </>
      );
    }
  };
}


export function youreAlwaysCoolHOC1(ChildComp: React.ComponentType<any >) {
