        // const themeCont = useContext(ThemeContext);
        // const colorFont = (themeCont == 'dark') ?'modal show  text-light':'modal show text-dark';  

        return (

            <MDBContainer >
              <MDBCard>
                <MDBCardBody>
                   <ChildComp {...this.props}/>
                  </MDBCardBody>
              </MDBCard>
            </MDBContainer>
        );
      }
    };
  }


