import React from "react";
import { ComponentType, useCallback, useState } from "react";

interface WithSubscriptionProps {
    data: any;
  }

  export  function withSubscription<
    T extends WithSubscriptionProps = WithSubscriptionProps
  >(WrappedComponent: React.ComponentType<T>) {
    class WithSubscription extends React.Component {
      /* ... */
      public static displayName = `WithSubscription(${getDisplayName(
        WrappedComponent
      )})`;
    }
    return WithSubscription;
  }

 export function getDisplayName<T>(WrappedComponent: React.ComponentType<T>) {
    return WrappedComponent.displayName || WrappedComponent.name || "Component";
  }



  interface ContainerProps {
    areYouCool: boolean;
  }

  const Container: React.FC<ContainerProps> = props => (
    <h1>{props.areYouCool ? "Heck yes I'm cool! :)" : ":("}</h1>
  );


export function withTimer<T>(Component: ComponentType<T>) {
  return (hocProps: Omit<T, "count" | "startTimer" | "endTimer">) => {
    const [count, setCount] = useState(0);
    const [timer, setTimer] = useState(-1);

    const startTimer = useCallback(() => {
      const timer = setInterval(
        () =>
          setCount((previous) => {
            return previous + 1;
          }),
        1000
      );
      setTimer(timer);
    }, []);

    const endTimer = useCallback(() => {
      clearInterval(timer);
      setCount(0);
    }, [timer]);

    return (
      <Component
        {...(hocProps as T)}
        startTimer={startTimer}
        endTimer={endTimer}
        count={count}
      />
    );
  };
}
