//   children?: JSX.Element;
// }

interface iTheme{
    name: string
}


interface AwaysCoolStateProps {
  text?: string;
}

export function withCard(ChildComp: React.ComponentType<any >) {
    return class Component<T> extends React.Component< T&iTheme> 
    {
     render() {
        // const { ...compProps } = this.props;
        // const json = JSON.stringify(this.props);
        // const json1 = JSON.stringify(compProps);
        // const themeCont = useContext(ThemeContext);
        // const colorFont = (themeCont == 'dark') ?'modal show  text-light':'modal show text-dark';  

        return (
            <MDBContainer >
              <MDBCard>
                <MDBCardBody>
                   <ChildComp {...this.props}/>
                  </MDBCardBody>
              </MDBCard>
            </MDBContainer>
        );
      }
    };
  }


export const App1 = withCard(MyApp);
export const AppReg = withTheme(withCard(RegistrationForm));
export const AppAbout = withTheme(withCard(About));
export const AppMain = withTheme(withCard(Main));
export const AppNotFound = withTheme(withCard(NotFound));
