import React, { createContext } from "react";
import logo from "./logo.svg";
import "./App.css";
import MyApp from "./Components/Comp";
import User from "./Components/User";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import NotFound from "./Components/NotFound";
import NavbarBS from "./Components/NavbarBS";
import MenuUser from "./Components/MenuUser";
import Login from "./Components/Login";
import { useTheme } from "./Hooks/useTheme";
import notFound from "./Components/NotFound";
import { WrappedProps, withModal } from "./HOC/withModal";

export const ThemeContext = createContext<string>('');

function App() {


  const { theme, setTheme } = useTheme();

  const updateTheme = () => {
    if (theme == 'light')
      setTheme('dark');
     else
     setTheme('light');
  };

  type CompProps = {
    a: string;
  };
  // You need to manually pass the props of your component to the Wrapped
  // function, because it can't infer the type
  const Comp = withModal<CompProps>((props: { a: string | number | boolean | React.ReactElement<any, string | React.JSXElementConstructor<any>> | Iterable<React.ReactNode> | React.ReactPortal | null | undefined; b: string | number | boolean | React.ReactElement<any, string | React.JSXElementConstructor<any>> | Iterable<React.ReactNode> | React.ReactPortal | null | undefined; }) => {

    // props now has the type CompProps & WrappedProps
    return (
      <p>
        A: {props.a}
        <br />
        B: {props.b}
      </p>
    );
  });


  const ModalNotFound = withModal(NotFound);

  return (
    <ThemeContext.Provider value={theme}>
    <BrowserRouter>
      <MenuUser theme={theme} updateTheme={updateTheme} />
      <Routes>
        <Route path="/MyApp" element={<MyApp />} />
        <Route path="/Login" element={<Login />} />
        <Route path="*" element={<Comp a="Hello" />} />
      </Routes>
    </BrowserRouter>

    </ThemeContext.Provider>
  );
}

export default App;
