import { type } from "os";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import "bootstrap/dist/css/bootstrap.min.css";
import React, { createContext, useContext, useState } from "react";
import PropTypes from "prop-types";
import { FaThumbsUp } from 'react-icons/fa';
import login_white   from '../ico/login_white_24dp.svg';
import login_black   from '../ico/login_black_24dp.svg';
import {
  MDBContainer,
  MDBCol,
  MDBRow,
  MDBBtn,
  MDBIcon,
  MDBInput,
  MDBCheckbox,
} from "mdb-react-ui-kit";
import { Button, Form } from "react-bootstrap";


type tTheme = "light" | "dark";






export interface iShema {
  theme: string ;
  updateTheme: () => void;
}

type tMenu = {
  name: string;
  linkComp: string;
};

const listMenu: tMenu[] = [
  { name: "/MyApp", linkComp: "Home" },
  { name: "/Page2", linkComp: "Features" },
  { name: "/Login", linkComp: "Login" },
];

const Menu = () => {
  return listMenu.map((item) => {
    return <Nav.Link href={item.name}>{item.linkComp}</Nav.Link>;
  });
};

function ColorSchemesExample(props: iShema) {

  const project = () => {
    switch (props.theme) {
      case "dark":
        return (
          <Navbar bg={props.theme} data-bs-theme={props.theme}>
            <Container>
              <Navbar.Brand href="#home">Navbar</Navbar.Brand>
              <Nav className="me-auto">{Menu()}</Nav>
            </Container>


          <div className={`form-check form-switch text-light`}>
            <input className="form-check-input" type="checkbox" onChange={(e) => {
            props.updateTheme();
          }} id="flexSwitchCheckDefault" />
            <label className="form-check-label" htmlFor="flexSwitchCheckDefault">{props.theme}</label>
          </div>

            <Button
              variant="dark"
              style={{ display: "flex", justifyContent: "right" }}
            >
              Login
            </Button>

          </Navbar>
        );
      // case "dark2":
      //   return (
      //     <Navbar bg="primary" data-bs-theme="dark">
      //       <Container>
      //         <Navbar.Brand href="#home">Navbar</Navbar.Brand>
      //         <Nav className="me-auto">
      //           {Menu()}
      //         </Nav>
      //       </Container>
      //       <Button
      //         variant="info"
      //         style={{ display: "flex", justifyContent: "right" }}
      //       >
      //         Login
      //       </Button>
      //     </Navbar>
      //   );
      case "light":
        return (
          <Navbar bg="light" data-bs-theme="light">
            <Container>
              <Navbar.Brand href="#home">Navbar</Navbar.Brand>
              <Nav className="me-auto">
              {Menu()}
              </Nav>
            </Container>

            <div className={`form-check form-switch text-dark`}>
            <input className="form-check-input" type="checkbox" onChange={(e) => {
            props.updateTheme();
          }} id="flexSwitchCheckDefault" />
            <label className="form-check-label" htmlFor="flexSwitchCheckDefault">{props.theme}</label>
          </div>

            <Button
              variant="light"
              style={{ display: "flex", justifyContent: "right" }}
            >
               <img src={login_black} alt="React Logo" />
              Login
            </Button>
          </Navbar>
        );
      default:
        return <h1>No project match</h1>;
    }
  };
  return (
    <>
      <div>{project()} </div>
    </>
  );
}



export default ColorSchemesExample;
