import React, { useContext } from "react";
import MyApp, { Props } from "../Components/Comp";
import { MDBCard, MDBCardBody, MDBContainer } from "mdb-react-ui-kit";
import { title } from "process";
import { ThemeContext } from "../App";
import { Form } from "react-router-dom";

// interface ContainerProps {
//   areYouCool: boolean;
//   children?: JSX.Element;
// }

interface AwaysCoolStateProps {
  text?: string;
}

function youreAlwaysCoolHOC(ChildComp: React.ComponentType<any | string>) {
  return class Component extends React.Component<AwaysCoolStateProps & Props> {
     state = {
      areYouCool: false,
    };

    handleClick = () => this.setState({ areYouCool: true });

    render() {
      const { text, ...compProps } = this.props;  
      return (
        <>
          {/* <button onClick={this.handleClick}>Find yout if you're cool</button>
          */}
          <MDBContainer>
            <MDBCard>
              <MDBCardBody>
                {text} 
                <ChildComp areYouCool={this.state.areYouCool} compProps />
                </MDBCardBody>
            </MDBCard>
          </MDBContainer>
        </>
      );
    }
  };
}


function youreAlwaysCoolHOC1(ChildComp: React.ComponentType<any | string>) {
    return class Component<T> extends React.Component<AwaysCoolStateProps & T> 
    {
     render() {
        const { text, ...compProps } = this.props;
        const themeCont = useContext(ThemeContext);
        const colorFont = (themeCont == 'dark') ?'modal show  text-light':'modal show text-dark';  

        return (

            <MDBContainer className={colorFont}   data-bs-theme={themeCont}>
              <MDBCard>
                <MDBCardBody>
                  {text} 
                  <ChildComp  compProps />
                  </MDBCardBody>
              </MDBCard>
            </MDBContainer>
        );
      }
    };
  }


export const App1 = youreAlwaysCoolHOC1(MyApp);
