import { createContext, useContext, useState } from 'react';
import { isElementAccessExpression } from 'typescript';

type shema = 'light' | 'dark'|null;

type iContext = 
{
    color :shema
    updateTodo: () => void;
}

const defaultState: iContext = {
    color:'light' ,
    updateTodo: () => {},
  };

const ThemeContext = createContext<iContext>(defaultState);

type Props = {
    title?:string
    children: string | JSX.Element | JSX.Element[] 
  }


  export default function MyApp() {
    const [theme, setTheme] = useState<iContext>(defaultState);
    const theme1 = useContext(ThemeContext);
    return (
        <ThemeContext.Provider value={theme}>
            <Form />
            <label>
                <input
                    type="checkbox"
                    checked={theme.color === 'dark'}
                    onChange={(e) => {
                        theme1.updateTodo=()=>{
                          if   (e.target.checked)
                          {
                            theme1.color = 'light';
                          } 
                          else{
                            theme1.color = 'dark';
                          };
                          e.target.checked = !e.target.checked;   
                        };
                        //setTheme(
                        //    e.target.checked
                        //        ? 'dark'
                        //        : 'light'
                        //);
                    }}
                />
                Use dark mode
            </label>
        </ThemeContext.Provider>
    );
}

function Form() {
    const t = useContext(ThemeContext)
    return (
        <Panel title="Welcome">
            <input type="text" id="inputField" />
            <Button>Sign up</Button>
            <Button>Log in</Button>
        </Panel>
    );
}

function Panel({ title, children }:Props) {
    const theme = useContext(ThemeContext);
    const className = 'panel-' + theme;
    return (
        <section className={className}>
            <h1>{title}</h1>
            {children}
        </section>
    );
}

function Button({ children }:Props) {
    //const theme = 'fff';
    const theme = useContext<iContext>(ThemeContext);
    const className = 'button-' + theme;
    const onClick  = (event: React.MouseEvent<HTMLButtonElement>) => 
    {
      var inputField = document.getElementById("inputField") as HTMLInputElement | null;;
      if (inputField != null) {
        //alert(inputField.value); // ðŸ‘‰ "Initial Value";
        //setTheme('dark');
        alert(theme.color);
        }
      event.preventDefault();
    }
    return (
        <button className={className} onClick={onClick}>{children}</button>
    );
}
