import { createContext, useContext, useState } from 'react';

type iContext = 'light' | 'dark';

const ThemeContext = createContext<iContext>('light');

type Props = {
    title?:string
    children: string | JSX.Element | JSX.Element[] 
  }


  export default function MyApp() {
    const [theme, setTheme] = useState<iContext>('light');
    return (
        <ThemeContext.Provider value={theme}>
            <Form />
            <label>
                <input
                    type="checkbox"
                    checked={theme === 'dark'}
                    onChange={(e) => {
                        setTheme(
                            e.target.checked
                                ? 'dark'
                                : 'light'
                        );
                    }}
                />
                Use dark mode
            </label>
        </ThemeContext.Provider>
    );
}

function Form() {
    const t = useContext(ThemeContext)
    return (
        <Panel title="Welcome">
            <input type="text" id="inputField" />
            <Button>Sign up</Button>
            <Button>Log in</Button>
        </Panel>
    );
}

function Panel({ title, children }:Props) {
    const theme = useContext(ThemeContext);
    const className = 'panel-' + theme;
    return (
        <section className={className}>
            <h1>{title}</h1>
            {children}
        </section>
    );
}

function Button({ children }:Props) {
    //const theme = 'fff';
    const theme = useContext<iContext>(ThemeContext);
    const [theme1, setTheme1] = useState<iContext>(theme);
    const className = 'button-' + theme;
    const onClick  = (event: React.MouseEvent<HTMLButtonElement>) => 
    {
      var inputField = document.getElementById("inputField") as HTMLInputElement | null;;
      if (inputField != null) {
        //alert(inputField.value); // ðŸ‘‰ "Initial Value";
        //setTheme('dark');
        alert(theme);
        if (theme !='dark'){
            setTheme1('dark');}
        else{
            setTheme1('light');}
        }
      event.preventDefault();
    }
    return (
        <button className={className} onClick={onClick}>{children}</button>
    );
}
