import Card from 'react-bootstrap/Card';
import { ThemeContext } from '../App';
import { useContext } from 'react';
import React from 'react';
import NotFound from '../Components/NotFound';


function withTheme(ChildComp: React.ComponentType<any | string>) {
  return class Component<T> extends React.Component<T> {
    render() {
      const themeCont = useContext(ThemeContext);
      const colorFont =
        themeCont == "dark" ? "modal show  text-light" : "modal show text-dark";

      return (
        <div className="mh-100 mh-100">
          <Card bg={themeCont} data-bs-theme={themeCont} text="danger">
            <Card.Body className="w-100 h-100">
              <ChildComp props />
            </Card.Body>
          </Card>
        </div>
      );
    }
  };
};

export const App2 = withTheme(NotFound);
