import React, { useState } from "react";
import { Button, ButtonGroup, Col, Form, Row } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { getAuth, useAuthContext } from "../Hooks/useAuthContext";

function Login() {

    const { auth, setAuth } = useAuthContext();

    const navigate = useNavigate();

    const onClickCancel = (event: React.MouseEvent<HTMLButtonElement>) => {
    navigate("/MyApp");
    event.preventDefault();
  };
  const onClickReg = (event: React.MouseEvent<HTMLButtonElement>) => {
    navigate("/Registration");
    event.preventDefault();
  };

  let login:string;
  let password:string;
  //let error:string = '';
  const [error,setError] = useState('');
  const onClickOK = (event: React.MouseEvent<HTMLButtonElement>)=>{
    if (getAuth({login:login,password:password})){
        setError('');
        navigate("/MyApp");}
    else{
        setError( "Нету пользователя или пароль введен с ошибкой");
        alert(error)
    }
    event.preventDefault();
  }
  return (
    <div className="form">
      <div className="form-body">
        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
          <Form.Label>Login</Form.Label>
          <Form.Control size="lg" type="email" placeholder="name@example.com" onChange={e => { login = e.target.value }}/>
        </Form.Group>

        <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
          <Form.Label  >Password</Form.Label>
          <Form.Control size="lg" type="password" onChange={e => { password = e.target.value }} />
        </Form.Group>
        <div>{auth.login}</div>
        </div>
        {/* <div className="btn-group d-flex" role="group"> */}
        <div>{error}</div>
        <Row>
        <Col className="btn-group d-flex"><Button variant="primary" size="lg" onClick={onClickOK}>
            Ok
        </Button></Col>
        <Col className="btn-group d-flex"><Button variant="primary" size="lg" onClick={onClickCancel}>
            Cancel
        </Button></Col>
        <Col className="btn-group d-flex"><Button variant="primary" size="lg" onClick={onClickReg}>
            Register
        </Button></Col>
      </Row>
    </div>

  );
}

export default Login;
